select m.time at time zone 'America/Phoenix' as t, m.mood as y, c.comment as c from marks m left join comments c on ( m.mark_id = c.mark_id ) where m.user_id = $1 and extract(day from m.time at time zone 'America/Phoenix') = extract(day from now() at time zone 'America/Phoenix') order by m.mark_id;
-- select m.time as t, m.mood as y, c.comment as c from marks m left join comments c on ( m.mark_id = c.mark_id ) where m.user_id = $1 and time >= now()::date at time zone 'America/Phoenix' + interval '1h' order by m.mark_id;
